(function () {
    app.modals.CreateOrEditWebhookSubscriptionModal = function () {
        var _modalManager;
        var _webhookSubscriptionService = abp.services.app.webhookSubscription;

        this.init = function (modalManager) {
            _modalManager = modalManager;
            _modalManager.getModal().find('#Webhooks').select2();
        };

        this.save = function () {
            var subscription = {
                id: _modalManager.getModal().find("input[name=Id]").val(),
                webhookUri: _modalManager.getModal().find("#webhookEndpointURL").val(),
                webhooks: _modalManager.getModal().find("#Webhooks").val(),
                headers: createOrEditHeaders
            };

            _modalManager.setBusy(true);

            if (createOrEditIsEdit) {
                _webhookSubscriptionService.updateSubscription(subscription)
                    .done(function () {
                        abp.notify.info(app.localize('SavedSuccessfully'));
                        _modalManager.close();
                        abp.event.trigger('app.createOrEditWebhookSubscriptionModalSaved');
                    }).always(function () {
                        _modalManager.setBusy(false);
                    });
            } else {
                _webhookSubscriptionService.addSubscription(subscription)
                    .done(function () {
                        abp.notify.info(app.localize('SavedSuccessfully'));
                        _modalManager.close();
                        abp.event.trigger('app.createOrEditWebhookSubscriptionModalSaved');
                    }).always(function () {
                        _modalManager.setBusy(false);
                    });
            }
        };

        $("#Btn_AddNewHeader").click(function () {
            AddNewHeader();
        });

        function AddNewHeader() {
            var headerKey = $("#CreateOrEditSubscription_HeaderKey").val();
            var headerValue = $("#CreateOrEditSubscription_HeaderValue").val();

            if (!headerKey || headerKey == '' || !headerValue || headerValue == '') {
                abp.notify.error(app.localize('HeaderKeyAndValueCanNotBeNull'));
                return;
            }

            if (createOrEditHeaders[headerKey]) {
                abp.notify.error(app.localize('HeaderKeysMustBeUnique'));
                return;
            }

            createOrEditHeaders[headerKey] = headerValue;
            addHeaderToListView(headerKey, headerValue);
        }

        function addHeaderToListView(key, value) {
            var row = `<div class="alert alert-custom alert-white m-1" id="additional-header-` + key + `" role="alert">
                        <div class="alert-text">[` + key + `, ` + value + `]</div>
                        <div class="alert-close">
                            <button type="button" class="close" data-dismiss="alert" aria-label="Close">
                                <span aria-hidden="true"><i class="la la-close"></i></span>
                            </button>
                        </div>
                    </div>`;
            $("#additional-header-list").append(row);
        }

        $(document).on('click', '#additional-header-list .alert-close button', function () {
            var parent = $(this).closest(".alert");
            var key = parent.attr("id").replace("additional-header-", "");

            if (createOrEditHeaders[key]) {
                delete createOrEditHeaders[key];
            }
        });
    };
})();
(function () {
    $(function () {
        var _table = $('#SubscriptionTable');
        var _webhookSubscriptionService = abp.services.app.webhookSubscription;

        var _permissions = {
            create: abp.auth.hasPermission('Pages.Administration.WebhookSubscription.Create'),
            edit: abp.auth.hasPermission('Pages.Administration.WebhookSubscription.Edit'),
            changeActivity: abp.auth.hasPermission('Pages.Administration.WebhookSubscription.ChangeActivity'),
            detail: abp.auth.hasPermission('Pages.Administration.WebhookSubscription.Detail')
        };

        var _createOrEditModal = new app.ModalManager({
            viewUrl: abp.appPath + 'Portal/WebhookSubscription/CreateModal',
            scriptUrl: abp.appPath + 'view-resources/Areas/Portal/Views/WebhookSubscriptions/_CreateOrEditModal.js',
            modalClass: 'CreateOrEditWebhookSubscriptionModal',
            cssClass: 'scrollable-modal'
        });

        var dataTable = _table.DataTable({
            paging: false,
            serverSide: false,
            processing: false,
            listAction: {
                ajaxFunction: _webhookSubscriptionService.getAllSubscriptions,
            },
            columnDefs: [
                {
                    className: 'control responsive',
                    orderable: false,
                    render: function () {
                        return '';
                    },
                    targets: 0
                },
                {
                    targets: 1,
                    data: "webhookUri",
                },
                {
                    targets: 2,
                    data: "webhooks",
                    render: function (webhooks) {
                        var result = "";
                        if (webhooks && webhooks.length > 0) {
                            for (var i = 0; i < webhooks.length; i++) {
                                if (i > 2) {
                                    result += ". . .";
                                    return result;
                                }
                                var webhook = webhooks[i];                             
                                result += webhook + "<br/>";
                            }
                        }
                        return result;
                    }
                },
                {
                    targets: 3,
                    data: "isActive",
                    render: function (isActive) {
                        var $span = $("<span/>").addClass("label");
                        if (isActive) {
                            $span.addClass("label label-success label-inline").text(app.localize('Yes'));
                        } else {
                            $span.addClass("label label-dark label-inline").text(app.localize('No'));
                        }

                        return $span[0].outerHTML;
                    }
                }
            ],
            drawCallback: function (settings) {
                if (_permissions.detail) {
                    _table.find('tbody tr').css("cursor", "pointer");
                }
            }
        });

        if (_permissions.detail) {
            _table.find('tbody').on('click', 'tr', function () {
                var data = dataTable.row(this).data();
                if(data){
                    window.location = "/Portal/WebhookSubscription/Detail/" + data.id;
                }
            });
        }

        $('#CreateNewWebhookSubscription').click(function () {
            _createOrEditModal.open();
        });

        $('#GetSubscriptionsButton').click(function (e) {
            e.preventDefault();
            getWebhooks();
        });

        function getWebhooks() {
            dataTable.ajax.reload();
        }

        abp.event.on('app.createOrEditWebhookSubscriptionModalSaved', function () {
            getWebhooks();
        });
    });
})();
